import React, { useState, useEffect } from 'react';
import styled from 'styled-components/native';
import CardBox from '../components/CardBox';
import QrButton from '../components/QrButton';
import Header from '../components/headers/Header';
import { theme } from '../colors/Theme';
import { decodeJWT } from 'did-jwt';

const Container = styled.View`
    flex : 1;
    align-items : center;
    background-color : ${theme.mono100};
    `;

const View = styled.View`
    flex : 1.5;
    justify-content : center;
    align-items : center;
    `;

const CardPage = ({ navigation, route, props }) => {
    const uid = route.params.uid;
    const userVC = route.params.userVC;
    const did = `${uid}`;
    const VC = `${userVC}`;
    const [userInfo, setUserInfo] = useState({});
     console.log(uid);
     console.log(did);

    const userFicture = route.params.userFicture;
    //const [ficture, setFicture] = useState(undefined);
    //console.log(userFicture);


    useEffect(() => {
        const decodedInfo = decodeJWT(VC).payload.vc.credentialSubject;
        console.log(decodedInfo);
        setUserInfo(decodedInfo);
    },[]);



    return (
        <Container>
            <Header
                //onPress={() => navigation.dispatch(DrawerActions.toggleDrawer())} 
                onPress={() => navigation.navigate("menuDrawer")}
            />
            <View>
                <CardBox
                    userID={`${userInfo.uid}`}
                    //source={{uri:ficture}}
                //onPress={() => navigation.navigate('AddCardPage', {userName:`${userName}`})}
                >
                </CardBox>
            </View>
            <View>
                <QrButton
                    title={'QR 생성'}
                    uid={uid}
                    userVC={userVC}
                    //userDID={userDID}
                    onPress={() => navigation.navigate('QRPage', { userID: `${userID}` })}
                >
                </QrButton>
            </View>
        </Container>
    );
};

export default CardPage;
