            onError={console.error}
        />
    )
    else
        return (
            <ThemeProvider theme={theme}>
                <Container>
                    <StatusBar></StatusBar>
                    <Navigation initialRoute={initialRoute} 
                                userVC={userVC} 
                                uid={uid}
                                //userFicture={userFicture}
                    />
                </Container>
            </ThemeProvider>
        );
};

export default App;
