import React, { useEffect, useState } from 'react';
import styled from 'styled-components/native';
import { Text } from 'react-native';
import ShadowButton from '../components/ShadowButton';
import { theme } from '../colors/Theme';
import Header from '../components/headers/Header';
import BackAlert from '../components/BackAlert';
import { decodeJWT } from 'did-jwt';

const Container = styled.View`
    flex : 1;
    justify-content : center;
    align-items : center;
    background-color : ${theme.mono100};
    `;

const View = styled.View`
    flex : 1.5;
    justify-content : center;
    align-items : center;
    `;

const CompleteAddCardPage = ({ navigation, route }) => {
    const uid = route.params.uid;
    const userVC = route.params.userVC;
    const abc = 'did:ethr:0x5b9bD7999577fa0cF57657f1d8C439b9BBD10267'
    const userName = undefined;
    const sample = 'eyJhbGciOiJFUzI1NksiLCJ0eXAiOiJKV1QifQ..EMDLromCh9J7TOMcb8NrQwGZ7MCXqx3iREDbBwRJ91MTDh4VVLHtifLLjcW5UYwf2iTy6LTdQW5TSV9HexvM9wA'
    const [userInfo, setUserInfo] = useState({});

    useEffect(() => {
        const decodedInfo = decodeJWT(sample).payload.vc.credentialSubject;
        console.log(decodedInfo);
        setUserInfo(decodedInfo);


    },[])


    BackAlert("사원 출입증 생성완료");

    return <Container>
            <Header nonMenu='nonMenu'/>
            <View>
                <Text style={{ fontSize: 20, color: theme.mono800 }}>
                    정상적으로 발급되었습니다.{`\n\n`}
                    이름: {`${userInfo.name && userInfo.name}\n\n`} 
                    사원 ID: {`${userInfo.uid && userInfo.uid}\n\n`}
                    회사명: {`${userInfo.company && userInfo.company}\n\n`}
                    DID: {`${abc}\n\n`}                
            </Text>
            </View>
            <View>
                <ShadowButton
                    title="확인"
                    onPress={() => navigation.reset({ routes: [{
                        name: 'CardPage', 
                        params: { userID: userID },
                    }]})}
                />
            </View>
        </Container>
};

export default CompleteAddCardPage;
