import React, { useState, useEffect } from 'react';
import styled from 'styled-components/native';
import { theme } from '../colors/Theme';
import SearchInput from '../components/SearchInput';
import IssueButton from '../components/IssueButton';
import AsyncStorage from '@react-native-async-storage/async-storage';
import DropDown from '../components/Dropdown-picker';

const Container = styled.View`
    flex : 1;
    align-items : center;
    background-color : ${theme.mono100};
    `;

const View = styled.View`
    flex : 1.5;
    align-items : center;
    `

const AddCardPage = ({ navigation, route }) => {
    const userDID = route.params.userDID;
    const [userID, setUserID] = useState('');
    const [company, setCompany] = useState('');
    const [location, setLocation] = useState('');


    const _addTask = () => {
            alert(`입력 : ${userID}`);
    };

    const _handleTextChangeID = text => {
        setUserID(text);
    };

    const _handleTextChangeCom = text => {
        setCompany(text);
    };

    const _handleTextChangeLo = text => {
        setLocation(text);
    };

    const _saveData = async (value) => {
        try {
            await AsyncStorage.setItem('tasks_id', value);
        } catch (e) {
            console.error(e);
        };
    };

    _saveData(userID); // ID 저장

    return (
        <Container>
            <View></View>
            <View>
                <SearchInput 
                    placeholder="회사명"
                    value={company}
                    onChangeText={_handleTextChangeCom}
                />
                <SearchInput 
                placeholder="지점"
                value={location}
                onChangeText={_handleTextChangeLo}
            />
                <SearchInput 
                    placeholder="사원 ID"
                    value={userID}
                    onChangeText={_handleTextChangeID}
                    onSubmitEditing={_addTask}
                />
            </View>
            <View style={{ justifyContent: 'center' }}>
                <IssueButton
                    title='발급하기'
                    uid={userID}
                    company={company}
                    location={location}

                    //userDID={userDID}
                    onPress={() => navigation.reset({ routes: [{
                        name: 'CompleteAddCardPage', 
                        params: { 
                            userID: userID, 
                            //userDID: userDID 
                        },
                    }]})}
                >
                </IssueButton>
            </View>
        </Container>
    );
};

export default AddCardPage;
