import React, { useEffect, useState } from 'react';
import { TouchableOpacity, StyleSheet, Text, Platform } from 'react-native';
import { theme } from '../colors/Theme';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';


const IssueButton = ({ onPress, ...props}) => {
    const title = props.title;
    const titleColor = '#fff';
    const userID = props.uid;
    const company = props.company;
    const location = props.location;
    const [exist, setExist] = useState(true);

    const _saveData = async (value) => {
        try {
            await AsyncStorage.setItem('tasks_vc', JSON.stringify({'vc': value[1],'identity': value[2]}));
        } catch (e) {
            console.error(e);
        };
    };
    //http:localhost:3000/issue/employee/
    //getUserDBInfo() 를 통해 value에 대한 데이터가 맞는지
    const createVC=(() => {
        axios.post('http://localhost:3000/issue/employee',{
            params: {
                uid : userID,
                company : company,
                location : location,
            }
        })
        .then(function (response) {
            _saveData(response);
            console.log(response);
        })
        .catch(function (error) {
            alert("정보가 없습니다");
            console.log(error);
        })
    });

    useEffect(() => {
        if(exist) {
            if(!userID) {
                setExist(false); // 처음 진입해서 id 입력 안할시
            };// 처음 진입해서 id 입력시
        } else {
            if(userID) { //지우고 다시 쓸때
                setExist(true);
            } else setExist(false); // 안쓴다면
        };
    },[exist,userID]);

    return exist ? (
        <TouchableOpacity style={styles.button}
                onPress = {() => {onPress(); createVC();}}
            >
                <Text style={[
                    styles.title,
                    { color: titleColor }
                ]}>{title} </Text>
            </TouchableOpacity>
    ) : (
        <TouchableOpacity style={styles.button}
                onPress = {() => alert("ID를 확인해주세요")}
            >
                <Text style={[
                    styles.title,
                    { color: titleColor }
                ]}>{title} </Text>
            </TouchableOpacity>
    )
};

const styles = StyleSheet.create({
    button: {
        backgroundColor: theme.blue900,
        width: 245,
        height: 65,
        alignItems: 'center',
        justifyContent: 'center',
        ...Platform.select({
            ios: {
                shadowColor: theme.mono800,
                shadowOffset: {
                    width: 10,
                    height: 10,
                },
                shadowOpacity: 0.5,
                shadowRadius: 10,
            },
            android: {
                elevation: 12,
            },
        }),
    },
    title: {
        fontSize: 30,
    },
});

export default IssueButton;
